:root {
--primary-color: #010712;
--secondary-color: #818386;
--bg-color: #FCFDFD;
--button-color: #3B3636;
--h1-color: #3F444C;
}

[data-theme="dark"] {
--primary-color: #FCFDFD;
--secondary-color: #818386;
--bg-color: #010712;
--button-color: #818386;
--h1-color: #FCFDFD;
}

* {
margin: 0;
box-sizing: border-box;
transition: all 0.3s ease-in-out;
}
a{
text-decoration: none;
}
button{
margin: auto;
}
.contact-container {
display: flex;
width: 100vw;
height: 100vh;
background: var(--bg-color);
}

.left-col {
width: 45vw;
height: 100%;
background-image: url("./assets/home/ads/home2.jpg");
background-size: cover;
background-repeat: no-repeat;
}

.logo {
width: 10rem;
padding: 1.5rem;
}

.right-col {
background: var(--bg-color);
width: 50vw;
height: 100vh;
padding: 3rem 3.5rem;
overflow: hidden;
}

h1, label, button, .description {
font-family: 'Jost', sans-serif;
font-weight: 400;
letter-spacing: 0.1rem;
}

h1 {
color:var(--h1-color);
text-transform: uppercase;
font-size: 2.5rem;
letter-spacing: 0.5rem;
font-weight: 300;
}

p {
color: var(--secondary-color);
font-size: 0.9rem;
letter-spacing: 0.01rem;
width: 40vw;
margin: 0.25rem 0;
}

label, .description {
color: var(--secondary-color);
text-transform: uppercase;
font-size: 0.625rem;
}

input, textarea, label {
width: 40vw;
display: block;
}

p, textarea {
font-family: 'Helvetica Neue', sans-serif;
}

textarea {
color: var(--primary-color);
font-weight: 500;
background: var(--bg-color);
border: none;
border-bottom: 1px solid var(--secondary-color);
padding: 0.5rem 0;
margin-bottom: 1rem;
outline: none;
}

textarea {
resize: none;
}





/* Toggle Switch */

.theme-switch-wrapper {
display: flex;
align-items: center;
text-align: center;
width: 160px;
position: absolute;
top: 0.5rem;
right: 0;
}

.description {
margin-left: 1.25rem;
}

.theme-switch {
display: inline-block;
height: 34px;
position: relative;
width: 60px;
}

.theme-switch input {
display:none;
}

.slider {
background-color: #ccc;
bottom: 0;
cursor: pointer;
left: 0;
position: absolute;
right: 0;
top: 0;
transition: .4s;
}

.slider:before {
background-color: #fff;
bottom: 0.25rem;
content: "";
width: 26px;
height: 26px;
left: 0.25rem;
position: absolute;
transition: .4s;
}

input:checked + .slider {
background-color: var(--button-color);
}

input:checked + .slider:before {
transform: translateX(26px);
}

.slider.round {
border-radius: 34px;
}

.slider.round:before {
border-radius: 50%;
}

#error, #success-msg {
width: 40vw;
margin: 0.125rem 0;
font-size: 0.75rem;
text-transform: uppercase;
font-family: 'Jost';
color: var(--secondary-color);
}
}

#success-msg {
transition-delay: 3s;
}
.contact{
width: 400px;

margin: auto;

}
.contact .bu{
display: flex;
margin-top: 50%;
}
.contact button{
justify-content: center;
}
.bu a{
color: #010712;
}
.contact p{
display: block;
width: 100%;


}

@media only screen and (max-width: 950px) {
.logo {
width: 8rem;
}
h1 {
font-size: 1.75rem;
}
p {
font-size: 0.7rem;
}
input, textarea, button {
font-size: 0.65rem;
}
.description {
font-size: 0.3rem;
margin-left: 0.4rem;
}
button {
width: 7rem;
}
.theme-switch-wrapper {
width: 120px;
}
.theme-switch {
height: 28px;
width: 50px;
}

.theme-switch input {
display:none;
}

.slider:before {
background-color: #fff;
bottom: 0.25rem;
content: "";
width: 20px;
height: 20px;
left: 0.25rem;
position: absolute;
transition: .4s;
}
input:checked + .slider:before {
transform: translateX(16px);
}

.slider.round {
border-radius: 15px;
}

.slider.round:before {
border-radius: 50%;
}

}
